mapper.select:
	    select * from t_bug_report 


	    		and os = #{bug.os}


	    		and osVersion = #{bug.osVersion}


	    		and brand = #{bug.brand}


	    		and appVersionCode = #{bug.appVersionCode}


	    		and appVersionName = #{bug.appVersionName}



             limit #{offset},#{limit}

select count(*) from t_bug_report 


		and os = #{bug.os}


		and osVersion = #{bug.osVersion}


		and brand = #{bug.brand}


		and appVersionCode = #{bug.appVersionCode}


		and appVersionName = #{bug.appVersionName}
