mapper.update:
		update t_folder set 


                name = #{name}


                isLock = #{isLock}


                sort = #{sort}


                deleteState = #{deleteState}


                modifyTime = #{modifyTime}


                count = #{count}


	    where 


		    	id = #{id}


	    		sid = #{sid}

mapper.select:
	    select * from t_folder 


	    		and userId = #{folder.userId}



             limit #{offset},#{limit}

	select * from t_folder 
	where 


	    	id = #{id}


    		sid = #{sid}
