mapper.update:
		update t_note_info set 


                title = #{title}


                content = #{content}


                remindTime = #{remindTime}


                folderSid = #{folderSid}


                kind = #{kind}


                deleteState = #{deleteState}


                modifyTime = #{modifyTime}


                hash = #{hash}


	    where 


		    	id = #{id}


	    		sid = #{sid}

mapper.select:
	    SELECT n.*, a.sid a_sid, a.createTime a_createTime, a.deleteState a_deleteState, a.description, a.filename, a.id a_id, 
	    a.mimeType, a.modifyTime a_modifyTime, a.type, a.hash a_hash, d.id d_id, d.checked, d.createTime d_createTime, 
	    d.deleteState d_deleteState, d.hash d_hash, d.modifyTime d_modifyTime, d.sid d_sid, d.sort, d.title d_title 
		FROM t_note_info n LEFT JOIN t_attach a ON n.sid = a.noteSid LEFT JOIN t_detail_list d on a.sid = d.noteSid 


	    		and userId = #{folder.userId}



             limit #{offset},#{limit}

	select * from t_note_info 
	where 


	    	id = #{id}


    		sid = #{sid}
